import './OnePlayerScreen.css'
import React, { useEffect, useState } from 'react'
import { wait } from '@testing-library/user-event/dist/utils';

const adj = [
  { 'l': 10, 'r': 1 },
  { 'l': 0, 'r': 2 },
  { 'l': 1, 'r': 3 },
  { 'l': 2, 'r': 4 },
  { 'l': 3, 'r': 11 },
  { 'l': 11, 'r': 6 },
  { 'l': 5, 'r': 7 },
  { 'l': 6, 'r': 8 },
  { 'l': 7, 'r': 9 },
  { 'l': 8, 'r': 10 },
  { 'l': 9, 'r': 0 },
  { 'l': 4, 'r': 5 }
]
const OnePlayerScreen = () => {

  const [valueDanBan, setValueDanBan] = useState([5, 5, 5, 5, 5]);
  const [valueDanMay, setValueDanMay] = useState([5, 5, 5, 5, 5]);
  const [valueQuan, setValueQuan] = useState([0, 0]); 
  const [isQuan, setIsQuan] = useState([1, 1]);
  const [hand, setHand] = useState(0);
  const [scoreBan, setScoreBan] = useState(0);
  const [scoreMay, setScoreMay] = useState(0);
  const [clickable, setClickable] = useState(true);
  const [turn, setTurn] = useState(1);
  const [idx, setIdx] = useState(-1);
  const [direction, setDirection] = useState('?')
  const [taking, setTaking] = useState(0);
  const [skipping, setSkipping] = useState(-1)

  let play = !clickable
  let take = taking
  let skip = skipping
  let instantIndex = idx
  let instantDir = direction

  console.log(idx)

  // setValueDanBan((id, val)=>{
  //   valueDanBan[id] += val;
  // })

  useEffect(() => {
    magic(idx, direction)
  }, [hand, take, skip])

  const handleValueDan = (index, value) => {
    if (index < 5) {
      let tmpArr = [...valueDanBan]
      tmpArr[index] += value
      setValueDanBan(tmpArr)
    }
    else if (index < 10) {
      let tmpArr = [...valueDanBan]
      tmpArr[index - 5] += value
      setValueDanMay(tmpArr)
    }
    else {
      let tmpArr = [...valueQuan]
      tmpArr[index - 10] += value
      setValueQuan(tmpArr)
    }
  }

  const handleScore = (value) => {
    if (turn == 1) {
      setScoreBan(scoreBan + value)
    }
    else {
      setScoreMay(scoreMay + value)
    }
  }

  const handleHand = (value) => {
    // console.log("before handle: " + `${hand}`);
    // console.log("value + hand: " + `${value + hand}`);
    setHand(hand + value);
    if (hand == 5) {
      // console.log("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa")
    }
    // console.log("afetr handle: " + `${hand}`);
  }

  const HandleClickable = () => {
    setClickable(!clickable)
  }

  const handleIsQuan = (index) => {
    let tmpArr = [...isQuan]
    tmpArr[index - 10] = 0;
    setIsQuan(tmpArr)
  }


  const getValueDan = (index) => {
    if (index < 5) {
      return valueDanBan[index]
    }
    if (index < 10) {
      return valueDanMay[index - 5]
    }
    return valueQuan[index - 10]
  }

  const checkIndex = (index) => {
    if (getValueDan(index) == 0) return true
  }

  const handleIdx = () =>{
    setIdx(adj[instantIndex][instantDir])
  }

  // const magic = (index, dir) => {
  //   console.log("wtf")
  //   console.log(getValueDan(index))
  //   if (checkIndex(index)) {
  //     console.log("vcl");
  //     return;
  //   }
  //   console.log("okok")
  //   setClickable(false);
  //   let play = 1, take = 0, skip = 1;
  //   while (play) {
  //     while (take) {
  //       if (skip) {
  //         if (checkIndex(index)) {
  //           index = adj[index][dir]
  //           skip = 0
  //         }
  //         else {
  //           play = 0;
  //           break;
  //         }
  //       }
  //       else {
  //         if (checkIndex(index)) {
  //           play = 0;
  //           break;
  //         }
  //         else {
  //           handleScore(getValueDan(index));
  //           skip = 1
  //         }
  //       }
  //     }
  //     if (!play) break;

  //     console.log("hand: " + `${hand}`);
  //     while (hand > 0) {
  //       console.log("index:" + index)
  //       wait(5000);
  //       handleHand(-1);
  //       handleValueDan(index, 1);
  //       index = adj[index][dir]
  //     }
  //     if (!checkIndex(index) && index < 10) {
  //       console.log("steal")
  //       handleHand(getValueDan(index));
  //       handleValueDan(index, -getValueDan(index))
  //       index = adj[index][dir]
  //       console.log("index:" + index);
  //       continue;
  //     }
  //     if (getValueDan) {
  //       take = 1;
  //       continue;
  //     }
  //     if (index > 9) {
  //       play = 0;
  //       break;
  //     }
  //   }
  //   setTurn(3 - turn);
  //   setClickable(true);
  // }

  const magic = (index, dir) => {
    instantIndex = index
    console.log("win")
    if(index == -1) index = idx;
    if(checkIndex(index)) {
      return;
    }
    setClickable(false);
    setDirection(dir);
    instantDir = dir;
    if(take){

    }

    if(play){
      if(hand){
        handleHand(-1)
        handleValueDan(index, 1)
        handleIdx()
        return
      }
      if(!checkIndex(index) && index<10){
        handleHand(getValueDan(index))
        handleValueDan(0-getValueDan(index))
        handleIdx()
        return
      }
      if(checkIndex(index)){
        setTaking(!taking);
        return;
      }
    }
  }

  return (
    <div>
      <div className='back'
        onClick={() => {
          console.log("start")
          magic(2, 'l');
          console.log("end")
        }}
      >
        ←
      </div>
      <div className='p2'>
        <div>Máy</div>
        <div>
          Điểm:
          {0}
        </div>
      </div>
      <div className='outer-board'>
        <div className='board'>
          <div className='nha-quan-l'>
            <div className='o-quan'>

            </div>
            <div className='o-quan-dan'
              onMouseEnter={() => {
                //console.log('hovered');
                let mid = document.getElementById("val-q0")
                mid.classList.remove('hide')
              }}
              onMouseLeave={() => {
                //console.log('left')
                let mid = document.getElementById("val-q0")
                mid.classList.add('hide')

              }}
            >
              <div id="val-q0" className='hide num-inside'>
                {valueQuan[0]}
              </div>
            </div>
          </div>

          {/* -------- khu dan ------------------- */}
          <div className='khu-dan'>
            <div className='nha-dan'>
              {
                valueDanBan.map((value, index) => {
                  let btn_l = 'btn-d' + `(${index})` + '-l';
                  //console.log(btn_l);
                  let btn_r = 'btn-d' + `(${index})` + '-r'
                  let val = 'val-d' + `(${index})`
                  return (
                    <div className='o-dan'
                      onMouseEnter={() => {
                        //console.log('hovered');
                        let l = document.getElementById(btn_l)
                        let mid = document.getElementById(val)
                        let r = document.getElementById(btn_r)

                        l.classList.remove('hide')
                        mid.classList.remove('hide')
                        r.classList.remove('hide')
                      }}
                      onMouseLeave={() => {
                        //console.log('left')
                        let l = document.getElementById(btn_l)
                        let mid = document.getElementById(val)
                        let r = document.getElementById(btn_r)

                        l.classList.add('hide')
                        mid.classList.add('hide')
                        r.classList.add('hide')

                      }}
                    >
                      <div id={btn_l} className='hide num-inside'
                        onClick={() => {
                          //console.log('click left')
                        }}
                      > {'<'}
                      </div>
                      <div id={val} className='hide num-inside'>
                        {value}
                      </div>
                      <div id={btn_r} className='hide num-inside'
                        onClick={() => {
                          //console.log('click right')
                        }}
                      > {'>'}
                      </div>
                    </div>
                  )

                })
              }
            </div>
            <div className='nha-dan'>
              {
                valueDanMay.map((value, index) => {
                  let btn_l = 'btn-m' + `(${index})` + '-l';
                  //console.log(btn_l);
                  let btn_r = 'btn-m' + `(${index})` + '-r'
                  let val = 'val-m' + `(${index})`
                  return (
                    <div className='o-dan'
                      onMouseEnter={() => {
                        //console.log('hovered');
                        let l = document.getElementById(btn_l)
                        let mid = document.getElementById(val)
                        let r = document.getElementById(btn_r)

                        l.classList.remove('hide')
                        mid.classList.remove('hide')
                        r.classList.remove('hide')
                      }}
                      onMouseLeave={() => {
                        //console.log('left')
                        let l = document.getElementById(btn_l)
                        let mid = document.getElementById(val)
                        let r = document.getElementById(btn_r)

                        l.classList.add('hide')
                        mid.classList.add('hide')
                        r.classList.add('hide')

                      }}
                    >
                      <div id={btn_l} className='hide num-inside'
                        onClick={() => {
                          //console.log('click left')
                        }}
                      > {'<'}
                      </div>
                      <div id={val} className='hide num-inside'>
                        {value}
                      </div>
                      <div id={btn_r} className='hide num-inside'
                        onClick={() => {
                          //console.log('click right')
                        }}
                      > {'>'}
                      </div>
                    </div>
                  )

                })
              }
            </div>
          </div>
          <div className='nha-quan-r'>
            <div className='o-quan'>

            </div>
            <div className='o-quan-dan'
              onMouseEnter={() => {
                //console.log('hovered');
                let mid = document.getElementById("val-q1")
                mid.classList.remove('hide')
              }}
              onMouseLeave={() => {
                //console.log('left')
                let mid = document.getElementById("val-q1")
                mid.classList.add('hide')

              }}
            >
              <div id="val-q1" className='hide num-inside'>
                {valueQuan[1]}
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className='p1'>
        <div>Bạn</div>
        <div>
          Điểm:
          {0}
        </div>
      </div>
    </div>
  )
}

export default OnePlayerScreen
