import './OnePlayerScreen.css'
import { useState } from 'react'

const OnePlayerScreen = () => {
  const [valueDanBan, setValueDanBan] = useState([5, 5, 5, 5, 5]);
  const [valueDanMay, setValueDanMay] = useState([5, 5, 5, 5, 5]);
  const [valueQuan, setValueQuan] = useState([0, 0]);
  const [isQuan, setIsQuan] = useState([1, 1]);
  const [hand, setHand] = useState(0);
  const [scoreBan, setScoreBan] = useState(0);
  const [scoreMay, setScoreMay] = useState(0);

  // setValueDanBan((id, val)=>{
  //   valueDanBan[id] += val;
  // })



  return (
    <div>
      <div className='back'>
        ←
      </div>
      <div className='p2'>
        <div>Máy</div>
        <div>
          Điểm:
          {0}
        </div>
      </div>
      <div className='outer-board'>
        <div className='board'>
          <div className='nha-quan-l'>
            <div className='o-quan'>

            </div>
            <div className='o-quan-dan'
              onMouseEnter={() => {
                console.log('hovered');
                let l = document.getElementById("btn-q0-l")
                let mid = document.getElementById("val-q0")
                let r = document.getElementById("btn-q0-r")

                l.classList.remove('hide')
                mid.classList.remove('hide')
                r.classList.remove('hide')
              }}
              onMouseLeave={() => {
                console.log('left')
                let l = document.getElementById("btn-q0-l")
                let mid = document.getElementById("val-q0")
                let r = document.getElementById("btn-q0-r")

                l.classList.add('hide')
                mid.classList.add('hide')
                r.classList.add('hide')

              }}
            >
              <div id="btn-q0-l" className='hide num-inside'
                onClick={() => {
                  console.log('click left')
                }}
              > {'<'}
              </div>
              <div id="val-q0" className='hide num-inside'>
                {valueDanMay[0]}
              </div>
              <div id="btn-q0-r" className='hide num-inside'
                onClick={() => {
                  console.log('click right')
                }}
              > {'>'}
              </div>
            </div>
          </div>
          <div className='khu-dan'>
            <div className='nha-dan'>
              {
                valueDanBan.map((value, index) => {
                  let ID = 'btn-d'+`(${index})`+'-l';
                  return (
                    <div className='o-dan'>
                      <div id="btn-q0-l" className='hide num-inside'
                        onClick={() => {
                          console.log('click left')
                        }}
                      > {'<'}
                      </div>
                      <div id="val-q0" className='hide num-inside'>
                        {valueDanMay[0]}
                      </div>
                      <div id="btn-q0-r" className='hide num-inside'
                        onClick={() => {
                          console.log('click right')
                        }}
                      > {'>'}
                      </div>
                    </div>
                  )

                })
              }
            </div>
            <div className='nha-dan'>
              {
                valueDanMay.map((value) => {
                  return (
                    <div className='o-dan'>
                      <div className='num-inside'>{value}</div>
                    </div>
                  )
                })
              }
            </div>
          </div>
          <div className='nha-quan-r'>
            <div className='o-quan'>

            </div>
            <div className='o-quan-dan'>
              <div className='num-inside'>{valueDanMay[1]}</div>
            </div>
          </div>
        </div>
      </div>
      <div className='p1'>
        <div>Bạn</div>
        <div>
          Điểm:
          {0}
        </div>
      </div>
    </div>
  )
}

export default OnePlayerScreen
